import { logs } from "./store.js";

const config = {
  tab: 1,
  max_loop_limit: 1000,
};

function console_log(string) {
  logs.update((log) => log + `${string}\n`);
}

export default function execute(input) {
  globalThis.scopes = {};
  scopes.global = [];
  scopes.vars = {};

  logs.set("");
  classifyScopes(input);
  for (const key in scopes) console.log(key, scopes[key]);
  runScope(scopes.global, scopes.vars);
}

// PROCESS
function classifyScopes(arr) {
  let scope_stack = ["global"];
  let last_depth = 0;
  let last_if_hash;

  for (let line of arr) {
    const depth = checkDepth(line);

    // check for comments
    if (line.includes("#")) {
      line = line.split("#")[0];
    }

    line = line.trim();
    // line not empty
    if (line) {
      const line_before = scopes[last(scope_stack)].slice(-1).pop();

      //no change
      if (last_depth == depth) scopes[last(scope_stack)].push(line);
      // came out
      else if (last_depth > depth) {
        for (const _ of Array(last_depth - depth)) scope_stack.pop();
        scopes[last(scope_stack)].push(line);
      }
      // diving into
      else if (last_depth < depth) {
        // new function
        if (line_before.startsWith("function")) {
          // remove declaration line
          scopes[last(scope_stack)].pop();

          let function_name = line_before.split(" ").filter(Boolean);
          function_name = function_name[1];

          scopes[function_name] = [line_before, line];
          scope_stack.push(function_name);
        }
        // if statments
        else if (line_before.startsWith("if")) {
          const hash_name = "@" + Date.now() + random(1000);

          // remove line before
          scopes[last(scope_stack)].pop();
          scopes[last(scope_stack)].push(`${hash_name}`);

          const if_hash_name = "@" + Date.now() + random(1000);
          scopes[if_hash_name] = [line];
          scope_stack.push(if_hash_name);

          last_if_hash = hash_name;
          scopes[hash_name] = [line_before, if_hash_name];
        }
        // else / else if
        else if (line_before.startsWith("else")) {
          scopes[last(scope_stack)].pop();

          const hash_name = "@" + Date.now() + random(1000);
          scope_stack.push(hash_name);
          scopes[hash_name] = [line];

          scopes[last_if_hash].push(line_before, hash_name);
        }

        // while / loops
        else {
          const hash_name = "@" + Date.now() + random(1000);

          scopes[last(scope_stack)].pop();
          scopes[last(scope_stack)].push(`${hash_name}`);

          scopes[hash_name] = [line_before, line];
          scope_stack.push(hash_name);
        }
      }
      last_depth = depth;
    }
  }
}

function checkDepth(line) {
  let count = 0;

  while (line.startsWith(" ")) {
    count++;
    line = line.substring(1);
  }

  return Math.floor(count / config.tab);
}

//  EXECUTION
function runScope(scope, vars = {}) {
  // run lines
  for (let lines of scope) {
    // check for loops / if
    if (lines.startsWith("@")) {
      const first_line = scopes[lines][0];

      if (first_line.startsWith("while")) whileLoop(lines, vars);
      else if (first_line.startsWith("loop")) basicLoop(lines, vars);
      else if (first_line.startsWith("if")) if_statement(lines, vars);
    } else {
      const output = runLine(lines, vars);
      if (lines.startsWith("return")) return value(output, vars);
    }
  }

  return null;
}

function runFunction(target, args) {
  // functions is empty
  if (!scopes[target]) {
    console.log("no such function " + target);
    return null;
  }
  const scope = scopes[target].slice();

  let line = scope.shift().split(" ").filter(Boolean);
  line = line.splice(2);

  // aruments for function
  const vars = {};
  for (const keyword of line) vars[keyword.substring(1)] = args.shift();
  return runScope(scope, vars);
}

function if_statement(hash_name, vars) {
  const statment = scopes[hash_name].slice();

  let hash;
  const count = statment.length / 2;
  for (const _ of new Array(count)) {
    let condition = statment.shift();

    condition = condition + " ";

    if (condition.startsWith("if")) {
      condition = "boolean " + condition.slice(2, -1);
      if (runLine(condition, vars)) {
        hash = statment.shift();
        break;
      }
    } else if (condition.startsWith("elseif")) {
      condition = "boolean " + condition.slice(6, -1);
      if (runLine(condition, vars)) {
        hash = statment.shift();
        break;
      }
    } else if (condition.startsWith("else")) {
      hash = statment.shift();
      break;
    }
    statment.shift();
  }

  if (hash) runScope(scopes[hash], vars);
}

function basicLoop(lines, vars) {
  let count = scopes[lines].slice();
  count = count.shift() + " ";
  count = value(count.slice(4, -1).trim(), vars);

  let x = config.max_loop_limit;
  while (count) {
    runScope(scopes[lines], vars);
    count--;
    if (!x) return alert(console.log("stack overflow"));
    else x--;
  }
}

function whileLoop(lines, vars) {
  let command = scopes[lines].slice();
  command = command.shift() + " ";

  command = "boolean" + command.slice(5, -1);

  let x = config.max_loop_limit;
  while (runLine(command, vars)) {
    runScope(scopes[lines], vars);
    if (!x) return alert(console.log("stack overflow"));
    else x--;
  }
}

function runLine(lines, vars) {
  lines = lines.split(" | ").reverse();
  // piping
  let output = "";
  for (let line of lines) {
    line = line.trim();

    if (line.startsWith("return")) {
      if (output != "") return output;
      else return line.split(" ").pop();
    }

    line += ` ${output}`;
    output = runStatement(line, vars);
  }

  return output;
}

function runStatement(line, vars) {
  line = line.split(" ").filter(Boolean);

  const command = line.shift();
  const target = line.shift();

  return runCommand(vars, command, target, line);
}

function runCommand(vars, command, target, line) {
  // basic command
  switch (command) {
    case "exit":
      process.exit();
    case "set":
      if (vars.hasOwnProperty(target))
        vars[target] = value(line.shift(), vars);
      else scopes.vars[target] = value(line.shift(), vars);
      return null;
    case "log":
      console_log(value(target, vars));
      return null;
    case "call":
      const args = [];
      for (const arg of line) args.push(value(arg, vars));
      return runFunction(target, args);
    case "boolean":
      return Boolean(value(target, vars));
    case "not":
      return !Boolean(value(target, vars));
  }

  // operators
  let sum;
  switch (command) {
    case "add":
      sum = value(target, vars);
      for (const num of line) sum += value(num, vars);
      return sum;
    case "subtract":
      sum = value(target, vars);
      for (const num of line) sum -= value(num, vars);
      return sum;
    case "multiply":
      sum = value(target, vars);
      for (const num of line) sum *= value(num, vars);
      return sum;
    case "divide":
      sum = value(target, vars);
      for (const num of line) sum /= value(num, vars);
      return sum;
    case "reminder":
      sum = value(target, vars);
      for (const num of line) sum = sum % value(num, vars);
      return sum;
    case "pow":
      sum = value(target, vars);
      return Math.pow(value(target, vars), value(line.shift(), vars));
  }

  // logic
  let b = line.shift();
  if (!b && b != 0) return null;
  let a = value(target, vars);
  b = value(b, vars);

  switch (command) {
    case "eq":
      return a == b;
    case "gt":
      return a > b;
    case "lt":
      return a < b;
    case "ge":
      return a >= b;
    case "le":
      return a <= b;
  }
}

// UTILL
function value(value, variables) {
  if (!value) return null;

  // check for negative
  if (value.startsWith("-$"))
    value = -1 * checkForVars(value.substring(1), variables);
  else value = checkForVars(value, variables);

  return checkType(value);
}

function checkType(value) {
  // check for type
  if (value == 0) {
    return 0;
  }
  if (Number(value)) return Number(value);

  if (value == "true") return true;
  if (value == "false") return false;
  if (value == "null") return null;

  return value;
}

function checkForVars(value, variables) {
  if (value.startsWith("$")) {
    if (variables.hasOwnProperty(value.substring(1)))
      return variables[value.substring(1)];
    return scopes.vars[value.substring(1)];
  } else return value;
}

function last(arr) {
  return arr[arr.length - 1];
}

